package p300plus.p438;

import java.util.*;

/**
 * <p>
 */
public class Solution {

    public List<Integer> findAnagrams(String s, String p) {
        if(p.length() > s.length()){
            return new ArrayList<>();
        }
        List<Integer> ans = new ArrayList<>();
        Map<Character,Integer> pCount = new HashMap<>();
        for(int i=0;i<p.length();i++){
            pCount.put(p.charAt(i),pCount.getOrDefault(p.charAt(i),0)+1);
        }

        int left = 0;
        int right = p.length()-1;

        Map<Character,Integer> currCount = new HashMap<>();
        for(int i=0;i<=right;i++){
            currCount.put(s.charAt(i),currCount.getOrDefault(s.charAt(i),0)+1);
        }

        while(right < s.length()){
            if(match(pCount,currCount)){
                ans.add(left);
            }
            if(right +1 < s.length()){
                currCount.put(s.charAt(left),currCount.get(s.charAt(left))-1);
                currCount.put(s.charAt(right +1),currCount.getOrDefault(s.charAt(right +1),0)+1);
            }
            left++;
            right++;
        }
        return ans;
    }

    private boolean match(Map<Character,Integer> pCount,Map<Character,Integer> currCount){
        Set<Character> keys = pCount.keySet();
        for(Character c : keys){
            if(pCount.get(c).equals(currCount.get(c))){
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        String s = "";
        String p = "";
        new Solution().findAnagrams(s,p);
    }

}
